SELECT     w.worker_id AS 外国人ID,     w.txtName AS 外国人名,     ro.txtName AS 登録支援機関の本部,     ro.txtPost AS 登録支援機関の郵便番号,     ro.txtAddress AS 登録支援機関の住所,     a.accept_id AS 所属機関ID,     a.txtName AS 所属機関名,     a.txtPost AS 所属機関郵便番号,     a.txtAddress AS 所属機関住所,     GROUP_CONCAT(ao.accept_office_id SEPARATOR ',') AS 所属事業所ID,     GROUP_CONCAT(ao.txtName SEPARATOR ',') AS 所属事業所名,     GROUP_CONCAT(ao.txtPost SEPARATOR ',') AS 所属事業所郵便番号,     GROUP_CONCAT(ao.txtAddress SEPARATOR ',') AS 所属事業所住所,     w.cmbRoudouMaster as 労働マスターID,     CASE aw.rdbPayOutType         WHEN 1 THEN '通貨払い'         WHEN 2 THEN '口座振り込み'         ELSE '不明'     END AS 支払区分,     aw.numBaseSalary AS 基本給,     aw.numKoteiZangyouPayment AS 固定残業代,     aw.numMonthSalary AS 月給,     aw.numDaySalary AS 日給,     aw.numHourSalary AS 時給,     CASE aw.rdbKyojyuhi         WHEN 1 THEN '有'         WHEN 2 THEN '無'         ELSE '不明'      END AS 居住費の徴収の有無,      aw.txtKyojyuhi AS １か月当たりの居住費徴収額,     CASE aw.rdbKyojyuType          WHEN 1 THEN '自己所有物件'          WHEN 2 THEN '借上物件'          ELSE '不明'     END AS 提供する宿泊施設の具体的な内容,          CASE aw.rdbWaterCost         WHEN 1 THEN '有'         WHEN 2 THEN '無'         ELSE '不明'      END AS 水道光熱費の徴収の有無,      aw.txtWaterCost AS 1か月あたりの水道光熱費,           CASE aw.rdbSyokuhi         WHEN 1 THEN '有'         WHEN 2 THEN '無'         ELSE '不明'      END AS `食事、食材等の提供の有無`,      aw.txtSyokuhi AS 1か月あたりの食費徴収額 FROM worker w LEFT JOIN accept a ON w.cmbAccept = a.accept_id LEFT JOIN accept_work_master aw ON aw.accept_work_master_id = w.cmbRoudouMaster LEFT JOIN worker_accept_office wao ON wao.worker_id = w.worker_id LEFT JOIN accept_office ao ON ao.accept_office_id = wao.accept_office_id LEFT JOIN regist_organ ro ON ro.regist_organ_id = a.cmbRegistOrgan  WHERE w.del_flg = 0 GROUP BY     w.worker_id ;